import React, { Component, useState } from 'react';
import './CalculatePayForm.css';
import TimePicker from 'react-time-picker'; //going with react-time-picker
//import '../../node_modules/jquery-timepicker/jquery.timepicker.js';
//import '../../node_modules/jquery-timepicker/jquery.timepicker.css';
//tried various timepickers, couldnt' get jquery timepicker to work

function CalculatePayForm () {

  const [startTime, changeStartTime] = useState('10:00');
  this.changeStartDateField = this.changeStartDateField.bind(this);
  this.changeBedTimeField = this.changeBedTimeField.bind(this);
  this.changeEndDateField = this.changeEndDateField.bind(this);

  return (
    <form onSubmit={this.calculatePay()} id="calcPayForm">
      <label>Start Time:</label>
      <TimePicker value={startTime} onChange={changeStartTime}/>
      <br/>
      <br/>
      <label>Bed Time:</label>
      <TimePicker value={this.state.bedTime} onChange={this.changeBedTimeField}/>
      <br/>
      <br/>
      <label>End Time:</label>
      <TimePicker value={this.state.endTime} onChange={this.changeEndDateField}/>
      <br/>
      <br/>
      <input type="submit" value="Calculate Pay" id="calcPayButton"/>
    </form>
  )
};

function calculatePay(){
  return 0;
};

function changeStartDateField(event){
  this.setState({startTime: event.target.value});
};

function changeBedTimeField(event){
  this.setState({bedTime: event.target.value});
};

function changeEndDateField(event){
  this.setState({endTime: event.target.value});
};

export default CalculatePayForm;
